            name: 'businessObjectFieldName',
            type: 'string',
            label: '字段名称',
          },
          {
            name: 'displayName',
            type: 'string',
            label: '字段显示名称',
          },
          {
            name: 'aliasName',
            type: 'string',
            label: '字段编码',
          },
          {
            name: 'componentType',
            type: 'string',
            label: '字段类型',
          },
        ],
      }),
    [],
  );

  const columns = React.useMemo(
    () => [
      {
        name: 'businessObjectFieldName',
        tooltip: 'overflow',
      },
      {
        name: 'displayName',
        tooltip: 'overflow',
      },
      {
        name: 'aliasName',
        tooltip: 'overflow',
      },
      {
        width: 100,
        name: 'componentType',
        tooltip: 'overflow',
        editor: true,
      },
    ],
    [],
  );

  return (
    <>
      <Button onClick={() => console.log(rightFieldInformationDs.toJSONData())}>
        toJSONData
      </Button>
      <Table
        mode="tree"
        selectionMode="treebox"
        dataSet={rightFieldInformationDs}
        columns={columns}
        style={{ height: 300 }}
        defaultRowExpanded
      />
    </>
  );
};

ReactDOM.render(<App />, document.getElementById('container'));

