import React from 'react';
import ReactDOM from 'react-dom';
import { Cascader, DataSet } from 'choerodon-ui/pro';

class App extends React.Component {
  data = [
    {
      value: 'zhejiang',
      eventKey: 'zhejiang',
      meaning: 'Zhejiang',
    },
    {
      value: 'hangzhou',
      eventKey: 'hangzhou',
      meaning: 'Hangzhou',
      parentValue: 'zhejiang',
    },
    {
      value: 'xihu',
      eventKey: 'xihu',
      meaning: 'West Lake',
      parentValue: 'hangzhou',
    },
    {
      value: 'jiangsu',
      eventKey: 'jiangsu',
      meaning: 'Jiangsu',
    },
    {
      value: 'nanjing',
      eventKey: 'nanjing',
      meaning: 'Nanjing',
      parentValue: 'jiangsu',
    },
    {
      value: 'zhonghuamen',
      eventKey: 'zhonghuamen',
      meaning: 'Zhong Hua Men',
      parentValue: 'nanjing',
    },
    {
      value: 'beijing-province',
      eventKey: 'beijing',
      meaning: 'Beijing',
    },
    {
      value: 'beijing-city',
      eventKey: 'beijing',
      meaning: 'Beijing',
      parentValue: 'beijing-province',
    },
  ];

  optionDs = new DataSet({
    autoQuery: false,
    selection: 'multiple',
    parentField: 'parentValue',
    idField: 'value',
    fields: [
      { name: 'value', type: 'string' },
      { name: 'eventKey', type: 'string' },
      { name: 'meaning', type: 'string' },
      { name: 'parentValue', type: 'string' },
    ],
    data: this.data,
  });

  ds = new DataSet({
    autoCreate: true,
    fields: [
      {
        name: 'id',
        type: 'string',
        textField: 'meaning',
        defaultValue: ['zhejiang', 'hangzhou', 'xihu'],
        valueField: 'value',
        label: '位置',
        options: this.optionDs,
      },
    ],
  });

  onChoose = (value, record) => {
    console.log(
      'onChoose: value: ',
      value,
      'record: ',
      record,
      ' eventKey: ',
      record.get('eventKey'),
    );
  };

  onChange = (value, oldValue) => {
    const eventKeys = this.getEventKeys(value);
    console.log(
      'onChange: value: ',
      value,
      'oldValue: ',
      oldValue,
      'eventKeys: ',
      eventKeys,
    );
  };

  getEventKeys = (values) => {
    const eventKeys = [];
    if (values && values.length > 0) {
      values.forEach((value) => {
        const record = this.optionDs.find((r) => r.get('value') === value);
        eventKeys.push(record && record.get('eventKey'));
      });
    }
    return eventKeys;
  };

