import React from 'react';
import ReactDOM from 'react-dom';
import { Steps, Button, message } from 'choerodon-ui';

const { Step } = Steps;

const stepsList = [];

for (let i = 1; i <= 17; i += 1) {
  stepsList.push({
    title: `导航-${i}`,
    content: `${i}`,
  });
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      current: 0,
    };
  }

  onChange = (current) => {
    this.setState({
      current,
    });
  };

  onClickItem = (e) => {
    console.log(e);
  };

  next() {
    const { current } = this.state;
    this.setState({ current: current + 1 });
  }

  prev() {
    const { current } = this.state;
    this.setState({ current: current - 1 });
  }

  render() {
    const { current } = this.state;
    return (
      <div>
        <div>
          <Steps
            current={current}
            type="navigation"
            onClick={this.onClickItem}
            onChange={this.onChange}
          >
            {stepsList.map((item) => (
              <Step
                key={item.title}
                title={item.title}
                onClick={() => message.success(item.title)}
              />
            ))}
          </Steps>
        </div>
        <div className="steps-content">{stepsList[current].content}</div>
        <div className="steps-action">
          {current < stepsList.length - 1 && (
            <Button type="primary" onClick={() => this.next()}>
              Next
            </Button>
          )}
          {current === stepsList.length - 1 && (
            <Button
              type="primary"
              onClick={() => message.success('Processing complete!')}
            >
              Done
            </Button>
          )}
          {current > 0 && (
            <Button style={{ marginLeft: 8 }} onClick={() => this.prev()}>
              Previous
            </Button>
          )}
        </div>
      </div>
    );
  }
}

